package com.splitit.splitit;

import java.time.LocalDate;
import java.time.LocalDateTime;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.ApplicationArguments;
import org.springframework.boot.ApplicationRunner;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.stereotype.Component;

import com.splitit.splitit.backend.DAO.Categoria;
import com.splitit.splitit.backend.DAO.CategoriaGlobal;
import com.splitit.splitit.backend.DAO.Parametro;
import com.splitit.splitit.backend.DAO.UsuarioAdministrador;
import com.splitit.splitit.backend.Repository.CategoriaRespository;
import com.splitit.splitit.backend.Repository.ParametroRepository;
import com.splitit.splitit.backend.Repository.UsuarioRepository;

@Component
public class DataLoader implements ApplicationRunner {
	
	@Autowired
	private PasswordEncoder passwordEncoder;
	
	@Autowired
	private UsuarioRepository usuarioRepo;
	
	@Autowired
	private CategoriaRespository categoriasRepo;
	
	@Autowired
	private ParametroRepository parametroRepo;
	
	public void run(final ApplicationArguments args) {
		if (usuarioRepo.count() == 0) {
			crearUsuarioAdmin();
		}
		if (categoriasRepo.count() == 0) {
			crearCategorias();
		}
		if (parametroRepo.count() == 0) {
			crearParametrosDelSistema();
		}
	}
	
	private void crearParametrosDelSistema() {
		Parametro parametroProtocolo = new Parametro("PROTOCOLO_FRONTEND", "http");
		Parametro parametroHost = new Parametro("HOST_FRONTEND", "split-it.net");
		Parametro parametroPuerto = new Parametro("PUERTO_FRONTEND", "80");
		
		parametroRepo.save(parametroProtocolo);
		parametroRepo.save(parametroHost);
		parametroRepo.save(parametroPuerto);
	}
	
	private void crearCategorias() {
		CategoriaGlobal alojamiento = new CategoriaGlobal(
				"Alojamiento",
				"city",
				"",
				true
				);
		CategoriaGlobal entretenimiento = new CategoriaGlobal(
				"Entretenimiento",
				"google-controller",
				"",
				true
				);
		CategoriaGlobal restaurante = new CategoriaGlobal(
				"Restaurante",
				"silverware-fork-knife",
				"",
				true
				);
		CategoriaGlobal supermercado = new CategoriaGlobal(
				"Supermercado",
				"basket",
				"",
				true
				);
		CategoriaGlobal transporte = new CategoriaGlobal(
				"Transporte",
				"car-side",
				"",
				true
				);
		categoriasRepo.save(alojamiento);
		categoriasRepo.save(entretenimiento);
		categoriasRepo.save(restaurante);
		categoriasRepo.save(supermercado);
		categoriasRepo.save(transporte);
	}
	
	private void crearUsuarioAdmin() {
		String imagenAdmin = "";
		UsuarioAdministrador admin = new UsuarioAdministrador(
				"Usuario",
				"Administrador",
				"admin@admin.com",
				LocalDate.now(),
				LocalDateTime.now(),
				this.passwordEncoder.encode("admin123"),
				imagenAdmin,
				null
				);
		usuarioRepo.save(admin);
	}
}
